export const onFillVlaue = ({
  value = {},
  layout,
  fullVal,
  isMerge = false,
  isVerify = false,
}) => {
  const fullValue = fullVal ?? value;
  return layout.reduce((p, cur) => {
    let isWrong = false;
    const { key, dataType, childConfig, childLayout, columns } = cur || {};
    const data = value?.[key] ?? DEFAULT_VALUES[dataType] ?? null;

    if (key) {
      if (dataType === 'chunkTable') {
        const isRequired = handleRequired(cur, fullValue); // 局部验证数据
        p[key] = (data ?? []).map((x) => {
          return onFillVlaue({
            value: x,
            layout: childConfig?.[x?.keyType] ?? childLayout ?? [],
            fullVal: x,
            isMerge: true,
            isVerify: isVerify && isRequired,
          });
        });
      } else if (dataType === 'virtualTabel') {
        const isRequired = handleRequired(cur, fullValue); // 局部验证数据
        p[key] = (data ?? []).map((x) => {
          const obj = {
            ...(cur?.defaultValues ?? {}),
            ...(cur?.defaultValuesObj?.[x.key] ?? {}),
          };
          const keys = _.intersection(Object.keys(obj), Object.keys(x));
          return onFillVlaue({
            value: x,
            layout: columns.filter((y) => keys.includes(y.key)),
            fullVal: x,
            isMerge: true,
            isVerify: isVerify && isRequired,
          });
        });
      } else {
        p[key] = data;
        if (isVerify) {
          isWrong = handleVerify(cur, data, fullValue); // 验证数据
        }
      }
    }
    // 如果验证失败，立即抛出错误，终止执行
    if (isWrong) {
      throw {
        data: data,
        fullValue: fullValue,
        item: cur,
      };
    }

    // 如果是合并数据，返回合并后的对象
    if (isMerge) {
      return { ...value, ...p };
    }

    return p;
  }, {});
};

function isFalsyCustom(value) {
  if (value === null || value === undefined) {
    return true;
  }
  if (typeof value === 'string' && value === '') {
    return true;
  }
  if (Array.isArray(value) && value.length === 0) {
    return true;
  }
  if (
    typeof value === 'object' &&
    !Array.isArray(value) &&
    Object.keys(value).length === 0
  ) {
    return true;
  }
  return false;
}

const handleVerify = (item, value, fullValue) => {
  const isRequired = handleRequired(item, fullValue);
  if (isRequired) return isFalsyCustom(value);
  return false;
};

const handleRequired = (item, fullValue) => {
  if (item?.notRequired) return false;
  if (item?.disabledUniteObj) {
    return Object.keys(item.disabledUniteObj).every((key) =>
      (item.disabledUniteObj[key]?.values ?? []).includes(fullValue[key]),
    );
  }
  if (item?.disabledObj) {
    return item.disabledObj.values.includes(fullValue[item.disabledObj.key]);
  }
  return true;
};
